package net.civeira.phylax.bootstrap.security.analyzers;

import java.util.regex.Pattern;

import jakarta.enterprise.context.ApplicationScoped;
import net.civeira.phylax.bootstrap.security.MaliciousInjectionRiskAnalizer;

@ApplicationScoped
public class LdapInjectionAnalyzer implements MaliciousInjectionRiskAnalizer {
  private static final Pattern LDAP_INJECTION_PATTERN = Pattern.compile(
      "(?i)(\\(&|\\|\\(|!|\\*\\)|\\b(objectClass|uid|cn)=\\*|\\b(|\\)|\\()(objectClass=\\*|uid=\\*|cn=\\*).*)");

  @Override
  public int analyze(String input) {
    return LDAP_INJECTION_PATTERN.matcher(input).find() ? 3 : 0;
  }

}
