package net.civeira.phylax.common.batch;

import java.util.function.BiConsumer;

import lombok.Builder;
import lombok.NonNull;

/**
 * A simple {@link Executor} implementation that delegates to a {@link BiConsumer} to execute the
 * step logic.
 *
 * @param <P> the type of step parameters
 */
@Builder
public class ExecutorByRun<P> implements Executor<P> {
  /** Functional logic to run during step execution. */
  @NonNull
  private final BiConsumer<Monitor, P> consumer;

  /**
   * Executes the consumer with the given monitor and parameters.
   */
  @Override
  public void run(BatchStepProgress result, Monitor t, P param) {
    consumer.accept(t, param);
  }
}
