package net.civeira.phylax.common.infrastructure.sql;

import java.sql.SQLException;
import java.util.Optional;

/**
 * Functional interface used to convert a {@link SqlResultSet} row into a domain-specific object.
 * <p>
 * Implementations of this interface define how to extract and map data from a single row of a
 * result set into an optional value of type {@code R}.
 * </p>
 *
 * @param <R> the type of the result object produced by the conversion
 */
@FunctionalInterface
public interface SqlConverter<R> {

  /**
   * Converts the given {@link SqlResultSet} into an instance of {@code R}.
   *
   * @param t the result set row to convert
   * @return an {@link Optional} containing the result, or an empty Optional if no result should be
   *         returned
   * @throws SQLException if a database access error occurs during conversion
   */
  Optional<R> convert(SqlResultSet t) throws SQLException;
}
