package net.civeira.phylax.features.access.apikeyclient.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.apikeyclient.domain.ApiKeyClient;
import net.civeira.phylax.features.access.apikeyclient.domain.event.ApiKeyClientEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class ApiKeyClientEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<ApiKeyClientEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final ApiKeyClient entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
