package net.civeira.phylax.features.access.apikeyclient.infrastructure.repository;

import java.util.Iterator;
import java.util.List;
import java.util.function.BiFunction;

import net.civeira.phylax.common.algorithms.Slider;
import net.civeira.phylax.features.access.apikeyclient.domain.ApiKeyClient;
import net.civeira.phylax.features.access.apikeyclient.domain.gateway.ApiKeyClientCursor;
import net.civeira.phylax.features.access.apikeyclient.domain.gateway.ApiKeyClientFilter;

class ApiKeyClientSlider extends Slider<ApiKeyClient> {

  /**
   * @autogenerated SlideGenerator
   */
  private final ApiKeyClientCursor cursor;

  /**
   * @autogenerated SlideGenerator
   */
  private final ApiKeyClientFilter filter;

  /**
   * @autogenerated SlideGenerator
   */
  private final BiFunction<ApiKeyClientFilter, ApiKeyClientCursor, Iterator<ApiKeyClient>> gateway;

  /**
   * @autogenerated SlideGenerator
   * @param multi
   * @param limit
   * @param gateway
   * @param filter
   * @param cursor
   */
  ApiKeyClientSlider(final Iterator<ApiKeyClient> multi, final int limit,
      final BiFunction<ApiKeyClientFilter, ApiKeyClientCursor, Iterator<ApiKeyClient>> gateway,
      final ApiKeyClientFilter filter, final ApiKeyClientCursor cursor) {
    super(multi, limit);
    this.gateway = gateway;
    this.filter = filter;
    this.cursor = cursor;
  }

  /**
   * @autogenerated SlideGenerator
   * @param apiKeyClients
   * @param limit
   * @return
   */
  @Override
  public Iterator<ApiKeyClient> next(List<ApiKeyClient> apiKeyClients, int limit) {
    ApiKeyClient last = apiKeyClients.get(apiKeyClients.size() - 1);
    ApiKeyClientCursor cr = this.cursor.withSinceUid(last.getUid()).withLimit(limit);
    return gateway.apply(this.filter, cr);
  }
}
