package net.civeira.phylax.features.access.loginprovider.application.policy;

import jakarta.enterprise.event.Observes;
import net.civeira.phylax.common.security.Actor;
import net.civeira.phylax.features.access.loginprovider.application.visibility.LoginProviderFixedFieldsCheck;

public class NonEditableTenant {

  /**
   * Constant for root field
   *
   * @autogenerated VisibilityPolicyGenerator
   */
  private static final String CLAIM_ROOT = "root";

  /**
   * @autogenerated VisibilityPolicyGenerator
   * @param fixed
   */
  public void fixedFields(@Observes LoginProviderFixedFieldsCheck fixed) {
    Actor actor = fixed.getQuery().getActor();
    if (!"true".equals(actor.getClaim(CLAIM_ROOT))) {
      fixed.add("tenant");
    }
  }
}
