package net.civeira.phylax.features.access.loginprovider.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.loginprovider.LoginProvider;
import net.civeira.phylax.features.access.loginprovider.event.LoginProviderEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class LoginProviderEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<LoginProviderEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final LoginProvider entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
