package net.civeira.phylax.features.access.loginprovider.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.loginprovider.LoginProvider;

@ApplicationScoped
@RequiredArgsConstructor
public class LoginProviderDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<LoginProviderDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public LoginProvider delete(final LoginProvider original, final Supplier<LoginProvider> last) {
    Function<LoginProviderDeleteTransition, LoginProvider> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<LoginProviderDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<LoginProviderDeleteTransition, LoginProvider> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(LoginProviderDeleteTransition.builder().original(original).build());
  }
}
