package net.civeira.phylax.features.access.loginprovider.transition.disable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.loginprovider.LoginProvider;
import net.civeira.phylax.features.access.loginprovider.transition.LoginProviderChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class LoginProviderDisablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<LoginProviderDisableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public LoginProvider disable(final LoginProvider original,
      final Function<LoginProviderChangeProposal, LoginProvider> last) {
    Function<LoginProviderDisableTransition, LoginProvider> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<LoginProviderDisableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<LoginProviderDisableTransition, LoginProvider> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.disable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(LoginProviderDisableTransition.builder()
        .data(new LoginProviderChangeProposal(original)).original(original).build());
  }
}
