package net.civeira.phylax.features.access.relyingparty.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.relyingparty.domain.RelyingParty;
import net.civeira.phylax.features.access.relyingparty.domain.event.RelyingPartyEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class RelyingPartyEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<RelyingPartyEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final RelyingParty entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
