package net.civeira.phylax.features.access.relyingparty.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.relyingparty.RelyingParty;

@ApplicationScoped
@RequiredArgsConstructor
public class RelyingPartyDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<RelyingPartyDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public RelyingParty delete(final RelyingParty original, final Supplier<RelyingParty> last) {
    Function<RelyingPartyDeleteTransition, RelyingParty> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<RelyingPartyDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<RelyingPartyDeleteTransition, RelyingParty> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(RelyingPartyDeleteTransition.builder().original(original).build());
  }
}
