package net.civeira.phylax.features.access.relyingparty.transition.enable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.relyingparty.RelyingParty;
import net.civeira.phylax.features.access.relyingparty.transition.RelyingPartyChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class RelyingPartyEnablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<RelyingPartyEnableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public RelyingParty enable(final RelyingParty original,
      final Function<RelyingPartyChangeProposal, RelyingParty> last) {
    Function<RelyingPartyEnableTransition, RelyingParty> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<RelyingPartyEnableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<RelyingPartyEnableTransition, RelyingParty> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.enable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(RelyingPartyEnableTransition.builder()
        .data(new RelyingPartyChangeProposal(original)).original(original).build());
  }
}
