package net.civeira.phylax.features.access.role.application.visibility;

import java.util.function.Consumer;
import java.util.function.UnaryOperator;

import lombok.Builder;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.NonNull;
import lombok.With;
import net.civeira.phylax.common.security.Interaction;

@Data
@Builder(toBuilder = true)
@With
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class RoleVisibilityCheck {

  /**
   * @autogenerated VisibilityCheckGenerator
   */
  @NonNull
  private final Interaction interaction;

  /**
   * @autogenerated VisibilityCheckGenerator
   */
  @NonNull
  private RoleVisibilityFilter filter;

  /**
   * @autogenerated VisibilityCheckGenerator
   * @param mapper
   */
  public void map(UnaryOperator<RoleVisibilityFilter> mapper) {
    filter = mapper.apply(filter);
  }

  /**
   * @autogenerated VisibilityCheckGenerator
   * @param mapper
   */
  public void peek(Consumer<RoleVisibilityFilter> mapper) {
    mapper.accept(filter);
  }
}
