package net.civeira.phylax.features.access.role.infrastructure.driver.rest;

import java.util.List;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.ws.rs.core.Response;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.generated.openapi.api.RoleBatchApi;

@RequiredArgsConstructor
@ApplicationScoped
public class RoleBatchController implements RoleBatchApi {

  /**
   * Role
   *
   * @autogenerated BatchControllerGenerator
   */
  private final RoleDeleteController deleteController;

  /**
   * @autogenerated BatchControllerGenerator
   * @param uids
   * @param search
   * @param name
   * @param relyingParty
   * @param relyingPartys
   * @return
   */
  @Override
  public Response roleApiBatchDelete(final List<String> uids, final String search,
      final String name, final String relyingParty, final List<String> relyingPartys) {
    return deleteController.roleApiBatchDelete(uids, search, name, relyingParty, relyingPartys);
  }

  /**
   * @autogenerated BatchControllerGenerator
   * @param batchId
   * @return
   */
  @Override
  public Response roleApiBatchDeleteQuery(final String batchId) {
    return deleteController.roleApiBatchDeleteQuery(batchId);
  }
}
