package net.civeira.phylax.features.access.role.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.role.domain.Role;
import net.civeira.phylax.features.access.role.domain.event.RoleEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class RoleEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<RoleEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final Role entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
