package net.civeira.phylax.features.access.role.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.role.Role;

@ApplicationScoped
@RequiredArgsConstructor
public class RoleDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<RoleDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public Role delete(final Role original, final Supplier<Role> last) {
    Function<RoleDeleteTransition, Role> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<RoleDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<RoleDeleteTransition, Role> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(RoleDeleteTransition.builder().original(original).build());
  }
}
