package net.civeira.phylax.features.access.scopeassignation.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.scopeassignation.ScopeAssignation;
import net.civeira.phylax.features.access.scopeassignation.event.ScopeAssignationEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class ScopeAssignationEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<ScopeAssignationEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final ScopeAssignation entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
