package net.civeira.phylax.features.access.scopeassignation.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.scopeassignation.ScopeAssignation;

@ApplicationScoped
@RequiredArgsConstructor
public class ScopeAssignationDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<ScopeAssignationDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public ScopeAssignation delete(final ScopeAssignation original,
      final Supplier<ScopeAssignation> last) {
    Function<ScopeAssignationDeleteTransition, ScopeAssignation> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<ScopeAssignationDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<ScopeAssignationDeleteTransition, ScopeAssignation> pipeline =
        reversed.stream().reduce(finalHandler,
            (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(ScopeAssignationDeleteTransition.builder().original(original).build());
  }
}
