package net.civeira.phylax.features.access.securitydomain.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.securitydomain.SecurityDomain;
import net.civeira.phylax.features.access.securitydomain.event.SecurityDomainEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class SecurityDomainEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<SecurityDomainEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final SecurityDomain entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
