package net.civeira.phylax.features.access.securitydomain.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.securitydomain.SecurityDomain;

@ApplicationScoped
@RequiredArgsConstructor
public class SecurityDomainDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<SecurityDomainDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public SecurityDomain delete(final SecurityDomain original, final Supplier<SecurityDomain> last) {
    Function<SecurityDomainDeleteTransition, SecurityDomain> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<SecurityDomainDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<SecurityDomainDeleteTransition, SecurityDomain> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(SecurityDomainDeleteTransition.builder().original(original).build());
  }
}
