package net.civeira.phylax.features.access.securitydomain.transition.disable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.securitydomain.SecurityDomain;
import net.civeira.phylax.features.access.securitydomain.transition.SecurityDomainChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class SecurityDomainDisablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<SecurityDomainDisableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public SecurityDomain disable(final SecurityDomain original,
      final Function<SecurityDomainChangeProposal, SecurityDomain> last) {
    Function<SecurityDomainDisableTransition, SecurityDomain> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<SecurityDomainDisableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<SecurityDomainDisableTransition, SecurityDomain> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.disable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(SecurityDomainDisableTransition.builder()
        .data(new SecurityDomainChangeProposal(original)).original(original).build());
  }
}
