package net.civeira.phylax.features.access.securityscope.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.securityscope.domain.SecurityScope;
import net.civeira.phylax.features.access.securityscope.domain.event.SecurityScopeEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class SecurityScopeEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<SecurityScopeEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final SecurityScope entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
