package net.civeira.phylax.features.access.securityscope.transition.disable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.securityscope.SecurityScope;
import net.civeira.phylax.features.access.securityscope.transition.SecurityScopeChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class SecurityScopeDisablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<SecurityScopeDisableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public SecurityScope disable(final SecurityScope original,
      final Function<SecurityScopeChangeProposal, SecurityScope> last) {
    Function<SecurityScopeDisableTransition, SecurityScope> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<SecurityScopeDisableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<SecurityScopeDisableTransition, SecurityScope> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.disable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(SecurityScopeDisableTransition.builder()
        .data(new SecurityScopeChangeProposal(original)).original(original).build());
  }
}
