package net.civeira.phylax.features.access.tenant.application.policy;

import jakarta.enterprise.event.Observes;
import net.civeira.phylax.common.security.Actor;
import net.civeira.phylax.features.access.tenant.application.visibility.TenantVisibilityCheck;

public class TenantAccesible {

  /**
   * Constant for rootAdmin field
   *
   * @autogenerated VisibilityPolicyGenerator
   */
  private static final String ROL_ROOT_ADMIN = "root:admin";

  /**
   * @autogenerated VisibilityPolicyGenerator
   * @param filter
   */
  public void filterVisibles(@Observes TenantVisibilityCheck filter) {
    Actor actor = filter.getInteraction().getActor();
    if (!actor.hasRole(ROL_ROOT_ADMIN)) {
      filter.peek(fl -> fl.setTenantAccesible(actor.getTenant().orElse(null)));
    }
  }
}
