package net.civeira.phylax.features.access.tenant.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFail;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenant.domain.Tenant;

@RequiredArgsConstructor
public class DomainValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<DomainVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param domain
   * @return
   */
  public T domain(DomainVO domain) {
    value = Optional.of(domain);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param domain
   * @return
   */
  public T domain(Optional<DomainVO> domain) {
    value = domain;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param domain
   * @return
   */
  public T domain(String domain) {
    value = Optional.of(DomainVO.from(domain));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<String> getDomain() {
    return value.map(DomainVO::getDomain);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<DomainVO> getDomainValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public DomainVO readDomain(final Optional<Tenant> original, final ConstraintFailList fails) {
    DomainVO result = value.orElse(original.map(Tenant::getDomainValue).orElse(null));
    if (null == result) {
      fails.add(new ConstraintFail("REQUIRED", "domain", null));
    }
    return result;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetDomain() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetDomainIfMatches(final String field) {
    if ("domain".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
