package net.civeira.phylax.features.access.tenant.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenant.domain.Tenant;

@RequiredArgsConstructor
public class RootValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<RootVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<Boolean> getRoot() {
    return value.map(RootVO::isRoot);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<RootVO> getRootValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public RootVO readRoot(final Optional<Tenant> original, final ConstraintFailList fails) {
    return value.orElse(original.map(Tenant::getRootValue).orElseGet(RootVO::nullValue));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param root
   * @return
   */
  public T root(RootVO root) {
    value = Optional.of(root);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param root
   * @return
   */
  public T root(Optional<RootVO> root) {
    value = root;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param root
   * @return
   */
  public T root(Boolean root) {
    value = Optional.of(RootVO.from(root));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T rootNull() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetRoot() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetRootIfMatches(final String field) {
    if ("root".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
