package net.civeira.phylax.features.access.tenant.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenant.domain.Tenant;
import net.civeira.phylax.features.access.tenant.domain.event.TenantEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<TenantEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final Tenant entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
