package net.civeira.phylax.features.access.tenant.transition.enable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenant.Tenant;
import net.civeira.phylax.features.access.tenant.transition.TenantChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantEnablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<TenantEnableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public Tenant enable(final Tenant original, final Function<TenantChangeProposal, Tenant> last) {
    Function<TenantEnableTransition, Tenant> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<TenantEnableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<TenantEnableTransition, Tenant> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.enable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(TenantEnableTransition.builder().data(new TenantChangeProposal(original))
        .original(original).build());
  }
}
