package net.civeira.phylax.features.access.tenantconfig.application.policy;

import jakarta.enterprise.event.Observes;
import net.civeira.phylax.common.security.Actor;
import net.civeira.phylax.features.access.tenant.domain.TenantReference;
import net.civeira.phylax.features.access.tenantconfig.application.visibility.TenantConfigEntityEnrichment;

public class InheritTenantExcludingRoot {

  /**
   * Constant for rootAdmin field
   *
   * @autogenerated FormulaPolicyGenerator
   */
  private static final String ROL_ROOT_ADMIN = "root:admin";

  /**
   * @autogenerated FormulaPolicyGenerator
   * @param builder
   */
  public void calculate(@Observes TenantConfigEntityEnrichment builder) {
    Actor actor = builder.getInteraction().getActor();
    if (!actor.hasRole(ROL_ROOT_ADMIN)) {
      builder.peek(dto -> dto.tenant(TenantReference.of(actor.getTenant().orElse(null))));
    }
  }
}
