package net.civeira.phylax.features.access.tenantconfig.application.usecase.list;

import java.util.Optional;

import lombok.Builder;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.With;

@Data
@Builder(toBuilder = true)
@With
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class TenantConfigListCursor {

  /**
   * max number of result
   *
   * @autogenerated ListCursorGenerator
   */
  private Integer limit;

  /**
   * If these value is set start retrieving for those whos uid is greater than these value
   *
   * @autogenerated ListCursorGenerator
   */
  private String sinceUid;

  /**
   * @autogenerated ListCursorGenerator
   * @return
   */
  public Optional<Integer> getLimit() {
    return Optional.ofNullable(limit);
  }

  /**
   * @autogenerated ListCursorGenerator
   * @return
   */
  public Optional<String> getSinceUid() {
    return Optional.ofNullable(sinceUid);
  }
}
