package net.civeira.phylax.features.access.tenantconfig.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenantconfig.domain.TenantConfig;
import net.civeira.phylax.features.access.tenantconfig.domain.event.TenantConfigEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantConfigEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<TenantConfigEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final TenantConfig entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
