package net.civeira.phylax.features.access.tenantconfig.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenantconfig.TenantConfig;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantConfigDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<TenantConfigDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public TenantConfig delete(final TenantConfig original, final Supplier<TenantConfig> last) {
    Function<TenantConfigDeleteTransition, TenantConfig> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<TenantConfigDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<TenantConfigDeleteTransition, TenantConfig> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(TenantConfigDeleteTransition.builder().original(original).build());
  }
}
