package net.civeira.phylax.features.access.tenantloginprovider.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenantloginprovider.domain.TenantLoginProvider;

@RequiredArgsConstructor
public class CertificateValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<CertificateVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param certificate
   * @return
   */
  public T certificate(CertificateVO certificate) {
    value = Optional.of(certificate);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param certificate
   * @return
   */
  public T certificate(Optional<CertificateVO> certificate) {
    value = certificate;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param certificate
   * @return
   */
  public T certificate(String certificate) {
    value = Optional.of(CertificateVO.from(certificate));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T certificateNull() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<String> getCertificate() {
    return value.flatMap(CertificateVO::getCertificate);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<CertificateVO> getCertificateValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public CertificateVO readCertificate(final Optional<TenantLoginProvider> original,
      final ConstraintFailList fails) {
    return value.orElse(
        original.map(TenantLoginProvider::getCertificateValue).orElseGet(CertificateVO::nullValue));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetCertificate() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetCertificateIfMatches(final String field) {
    if ("certificate".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
