package net.civeira.phylax.features.access.tenantloginprovider.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenantloginprovider.domain.TenantLoginProvider;
import net.civeira.phylax.features.access.tenantloginprovider.domain.event.TenantLoginProviderEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantLoginProviderEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<TenantLoginProviderEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final TenantLoginProvider entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
