package net.civeira.phylax.features.access.tenanttermsofuse.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenanttermsofuse.domain.TenantTermsOfUse;

@RequiredArgsConstructor
public class AttachedValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<AttachedVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param attached
   * @return
   */
  public T attached(AttachedVO attached) {
    value = Optional.of(attached);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param attached
   * @return
   */
  public T attached(Optional<AttachedVO> attached) {
    value = attached;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param attached
   * @return
   */
  public T attached(String attached) {
    value = Optional.of(AttachedVO.from(attached));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T attachedNull() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<String> getAttached() {
    return value.flatMap(AttachedVO::getAttached);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<AttachedVO> getAttachedValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public AttachedVO readAttached(final Optional<TenantTermsOfUse> original,
      final ConstraintFailList fails) {
    return value
        .orElse(original.map(TenantTermsOfUse::getAttachedValue).orElseGet(AttachedVO::nullValue));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetAttached() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetAttachedIfMatches(final String field) {
    if ("attached".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
