package net.civeira.phylax.features.access.tenanttermsofuse.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFail;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenanttermsofuse.domain.TenantTermsOfUse;

@RequiredArgsConstructor
public class TextValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<TextVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<String> getText() {
    return value.map(TextVO::getText);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<TextVO> getTextValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public TextVO readText(final Optional<TenantTermsOfUse> original,
      final ConstraintFailList fails) {
    TextVO result = value.orElse(original.map(TenantTermsOfUse::getTextValue).orElse(null));
    if (null == result) {
      fails.add(new ConstraintFail("REQUIRED", "text", null));
    }
    return result;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param text
   * @return
   */
  public T text(TextVO text) {
    value = Optional.of(text);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param text
   * @return
   */
  public T text(Optional<TextVO> text) {
    value = text;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param text
   * @return
   */
  public T text(String text) {
    value = Optional.of(TextVO.from(text));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetText() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetTextIfMatches(final String field) {
    if ("text".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
