package net.civeira.phylax.features.access.tenanttermsofuse.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenanttermsofuse.TenantTermsOfUse;
import net.civeira.phylax.features.access.tenanttermsofuse.event.TenantTermsOfUseEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantTermsOfUseEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<TenantTermsOfUseEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final TenantTermsOfUse entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
