package net.civeira.phylax.features.access.tenanttermsofuse.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.tenanttermsofuse.TenantTermsOfUse;

@ApplicationScoped
@RequiredArgsConstructor
public class TenantTermsOfUseDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<TenantTermsOfUseDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public TenantTermsOfUse delete(final TenantTermsOfUse original,
      final Supplier<TenantTermsOfUse> last) {
    Function<TenantTermsOfUseDeleteTransition, TenantTermsOfUse> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<TenantTermsOfUseDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<TenantTermsOfUseDeleteTransition, TenantTermsOfUse> pipeline =
        reversed.stream().reduce(finalHandler,
            (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(TenantTermsOfUseDeleteTransition.builder().original(original).build());
  }
}
