package net.civeira.phylax.features.access.trustedclient.application.policy;

import jakarta.enterprise.event.Observes;
import net.civeira.phylax.common.security.Actor;
import net.civeira.phylax.features.access.trustedclient.application.usecase.update.TrustedClientUpdateAllowDecision;

public class UpdateOnlyForRoot {

  /**
   * Constant for rootAdmin field
   *
   * @autogenerated ConditionPolicyGenerator
   */
  private static final String ROL_ROOT_ADMIN = "root:admin";

  /**
   * @autogenerated ConditionPolicyGenerator
   * @param allowDecision
   */
  public void checkAllow(@Observes TrustedClientUpdateAllowDecision allowDecision) {
    Actor actor = allowDecision.getQuery().getActor();
    if (!actor.hasRole(ROL_ROOT_ADMIN)) {
      allowDecision.deny("Update only for root");
    }
  }
}
