package net.civeira.phylax.features.access.trustedclient.domain.gateway;

import java.util.List;
import java.util.Optional;

import lombok.Builder;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.With;

@Data
@Builder(toBuilder = true)
@With
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class TrustedClientCursor {

  /**
   * max number of result
   *
   * @autogenerated CursorGenerator
   */
  private Integer limit;

  /**
   * @autogenerated CursorGenerator
   */
  private List<TrustedClientOrder> order;

  /**
   * @autogenerated CursorGenerator
   */
  private String sinceCode;

  /**
   * If these value is set start retrieving for those whos uid is greater than these value
   *
   * @autogenerated CursorGenerator
   */
  private String sinceUid;

  /**
   * @autogenerated CursorGenerator
   * @return
   */
  public Optional<Integer> getLimit() {
    return Optional.ofNullable(limit);
  }

  /**
   * @autogenerated CursorGenerator
   * @return
   */
  public List<TrustedClientOrder> getOrder() {
    return null == order ? List.of() : List.copyOf(order);
  }

  /**
   * @autogenerated CursorGenerator
   * @return
   */
  public Optional<String> getSinceCode() {
    return Optional.ofNullable(sinceCode);
  }

  /**
   * @autogenerated CursorGenerator
   * @return
   */
  public Optional<String> getSinceUid() {
    return Optional.ofNullable(sinceUid);
  }
}
