package net.civeira.phylax.features.access.trustedclient.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFail;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.trustedclient.domain.TrustedClient;

@RequiredArgsConstructor
public class EnabledValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<EnabledVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param enabled
   * @return
   */
  public T enabled(EnabledVO enabled) {
    value = Optional.of(enabled);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param enabled
   * @return
   */
  public T enabled(Optional<EnabledVO> enabled) {
    value = enabled;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param enabled
   * @return
   */
  public T enabled(Boolean enabled) {
    value = Optional.of(EnabledVO.from(enabled));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<Boolean> getEnabled() {
    return value.map(EnabledVO::isEnabled);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<EnabledVO> getEnabledValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public EnabledVO readEnabled(final Optional<TrustedClient> original,
      final ConstraintFailList fails) {
    EnabledVO result = value.orElse(original.map(TrustedClient::getEnabledValue).orElse(null));
    if (null == result) {
      fails.add(new ConstraintFail("REQUIRED", "enabled", null));
    }
    return result;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetEnabled() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetEnabledIfMatches(final String field) {
    if ("enabled".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
