package net.civeira.phylax.features.access.trustedclient.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.trustedclient.domain.TrustedClient;
import net.civeira.phylax.features.access.trustedclient.domain.event.TrustedClientEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class TrustedClientEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<TrustedClientEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final TrustedClient entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
