package net.civeira.phylax.features.access.trustedclient.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.trustedclient.TrustedClient;

@ApplicationScoped
@RequiredArgsConstructor
public class TrustedClientDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<TrustedClientDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public TrustedClient delete(final TrustedClient original, final Supplier<TrustedClient> last) {
    Function<TrustedClientDeleteTransition, TrustedClient> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<TrustedClientDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<TrustedClientDeleteTransition, TrustedClient> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(TrustedClientDeleteTransition.builder().original(original).build());
  }
}
