package net.civeira.phylax.features.access.trustedclient.transition.enable;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.trustedclient.TrustedClient;
import net.civeira.phylax.features.access.trustedclient.transition.TrustedClientChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class TrustedClientEnablePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<TrustedClientEnableStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public TrustedClient enable(final TrustedClient original,
      final Function<TrustedClientChangeProposal, TrustedClient> last) {
    Function<TrustedClientEnableTransition, TrustedClient> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<TrustedClientEnableStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<TrustedClientEnableTransition, TrustedClient> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.enable(transition, next), (f1, f2) -> f1);
    return pipeline.apply(TrustedClientEnableTransition.builder()
        .data(new TrustedClientChangeProposal(original)).original(original).build());
  }
}
