package net.civeira.phylax.features.access.user.application.policy;

import jakarta.enterprise.event.Observes;
import net.civeira.phylax.common.security.Actor;
import net.civeira.phylax.features.access.user.application.visibility.UserFixedFieldsCheck;

public class FixTenantExcludingRoot {

  /**
   * Constant for rootAdmin field
   *
   * @autogenerated VisibilityPolicyGenerator
   */
  private static final String ROL_ROOT_ADMIN = "root:admin";

  /**
   * @autogenerated VisibilityPolicyGenerator
   * @param fixed
   */
  public void fixedFields(@Observes UserFixedFieldsCheck fixed) {
    Actor actor = fixed.getQuery().getActor();
    if (!actor.hasRole(ROL_ROOT_ADMIN)) {
      fixed.add("tenant");
    }
  }
}
