package net.civeira.phylax.features.access.user.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.user.domain.User;
import net.civeira.phylax.features.access.user.domain.UserApproveOptions;

@RequiredArgsConstructor
public class ApproveValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<ApproveVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param approve
   * @return
   */
  public T approve(ApproveVO approve) {
    value = Optional.of(approve);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param approve
   * @return
   */
  public T approve(Optional<ApproveVO> approve) {
    value = approve;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param approve
   * @return
   */
  public T approve(UserApproveOptions approve) {
    value = Optional.of(ApproveVO.from(approve));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T approveNull() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<UserApproveOptions> getApprove() {
    return value.flatMap(ApproveVO::getApprove);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<ApproveVO> getApproveValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public ApproveVO readApprove(final Optional<User> original, final ConstraintFailList fails) {
    return value.orElse(original.map(User::getApproveValue).orElseGet(ApproveVO::nullValue));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetApprove() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetApproveIfMatches(final String field) {
    if ("approve".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
