package net.civeira.phylax.features.access.user.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.crypto.AesCipherService;
import net.civeira.phylax.common.value.validation.ConstraintFail;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.user.domain.User;

@RequiredArgsConstructor
public class PasswordValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<PasswordVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @param cypher
   * @return
   */
  public Optional<String> getPasswordCyphered(AesCipherService cypher) {
    return value.map(val -> val.getPasswordCyphered(cypher));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param cypher
   * @return
   */
  public Optional<String> getPasswordPlain(AesCipherService cypher) {
    return value.map(val -> val.getPasswordPlain(cypher));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<PasswordVO> getPasswordValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param password
   * @return
   */
  public T password(PasswordVO password) {
    value = Optional.of(password);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param password
   * @return
   */
  public T password(Optional<PasswordVO> password) {
    value = password;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param password
   * @return
   */
  public T passwordCyphered(String password) {
    value = Optional.of(PasswordVO.fromCyphered(password));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param password
   * @return
   */
  public T passwordPlain(String password) {
    value = Optional.of(PasswordVO.fromPlain(password));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public PasswordVO readPassword(final Optional<User> original, final ConstraintFailList fails) {
    PasswordVO result = value.orElse(original.map(User::getPasswordValue).orElse(null));
    if (null == result) {
      fails.add(new ConstraintFail("REQUIRED", "password", null));
    }
    return result;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetPassword() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetPasswordIfMatches(final String field) {
    if ("password".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
