package net.civeira.phylax.features.access.user.domain.valueobject;

import java.util.Optional;

import lombok.RequiredArgsConstructor;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.user.domain.User;

@RequiredArgsConstructor
public class ProviderValueHolder<T> {

  /**
   * @autogenerated ValueHolderGenerator
   */
  private final T holder;

  /**
   * @autogenerated ValueHolderGenerator
   */
  private Optional<ProviderVO> value = Optional.empty();

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<String> getProvider() {
    return value.flatMap(ProviderVO::getProvider);
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public Optional<ProviderVO> getProviderValue() {
    return value;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param provider
   * @return
   */
  public T provider(ProviderVO provider) {
    value = Optional.of(provider);
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param provider
   * @return
   */
  public T provider(Optional<ProviderVO> provider) {
    value = provider;
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param provider
   * @return
   */
  public T provider(String provider) {
    value = Optional.of(ProviderVO.from(provider));
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T providerNull() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param original
   * @param fails
   * @return
   */
  public ProviderVO readProvider(final Optional<User> original, final ConstraintFailList fails) {
    return value.orElse(original.map(User::getProviderValue).orElseGet(ProviderVO::nullValue));
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @return
   */
  public T unsetProvider() {
    value = Optional.empty();
    return holder;
  }

  /**
   * @autogenerated ValueHolderGenerator
   * @param field
   * @return
   */
  public T unsetProviderIfMatches(final String field) {
    if ("provider".equals(field)) {
      value = Optional.empty();
    }
    return holder;
  }
}
