package net.civeira.phylax.features.access.user.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.domain.User;
import net.civeira.phylax.features.access.user.domain.event.UserEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class UserEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<UserEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final User entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
