package net.civeira.phylax.features.access.user.transition.block;

import java.time.OffsetDateTime;
import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.User;
import net.civeira.phylax.features.access.user.transition.UserChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class UserBlockPipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserBlockStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param blockedUntil
   * @param original
   * @param last
   * @return
   */
  public User block(final OffsetDateTime blockedUntil, final User original,
      final Function<UserChangeProposal, User> last) {
    Function<UserBlockTransition, User> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<UserBlockStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserBlockTransition, User> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.block(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserBlockTransition.builder().data(new UserChangeProposal(original))
        .original(original).build());
  }
}
